var settings = {
	key: '',
	host: 'localhost',
	port: 8192,
	data: {
		user: 'ryanshawty',
		site: 'mcserverstatus',
		addr: '192.168.1.1'
	}
};
settings.key = settings.key.replace(/ /g, '+');

var votifier = require('./index.js');

describe('votifier', function(){
    it('should send votifier data', function(done){
        votifier.send(settings, function(err){
           if(err) return done(err); 
           done();
        });
    });
});